import React, { useState } from 'react';
import { sidebarLinks } from '../../../data/dashboard-links';
import { logout } from '../../../services/operations/authAPI';
import { useDispatch, useSelector } from 'react-redux';
import { SidebarLink } from './SidebarLink';
import { useNavigate } from 'react-router-dom';
import { VscSignOut } from "react-icons/vsc";
import { ConfirmationModal } from '../../common/ConfirmationModal';

export const Sidebar = () => {

    const {user, loading: profileLoading} = useSelector( (state) => state.profile );
    const {loading: authLoading} = useSelector( (state) => state.auth );

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [confirmationModal, setConfirmationModal] = useState(null);

    if(authLoading || profileLoading) {
        return (
            <div className='text-2xl mt-24'>
                Loading...
            </div>
        );
    }

  return (
    <div>
        <div className='flex flex-col min-w-[222px] border-r-[1px] border-r-richblack-700
        h-[calc(100vh-3.5rem)] bg-richblack-800 py-10'>

            <div className='flex flex-col'>
                {
                    sidebarLinks.map( (link) => {
                        if(link.type && user?.accountType !== link.type) return null;
                        return (
                            <SidebarLink key={link.id} link={link} iconName={link.icon}/>
                        );
                    })
                }
            </div>

            <div className='mx-auto my-6 h-[1px] w-10/12 bg-richblack-600'></div>

            <div className='flex flex-col'>

                <sidebarLinks 
                    link={{name:"Settings", Path:"dashboard/settings"}}
                    iconName="VscSettingGear"
                />

                <button 
                    onClick={() => setConfirmationModal({
                        text1: "Are you sure ?",
                        text2: "Your account will be logged out",
                        btn1Text: "Logout",
                        btn2Text: "Cancel",
                        btn1Handler: () => dispatch(logout(navigate)),
                        btn2Handler: () => setConfirmationModal(null)
                    })}
                    className='text-sm font-medium text-richblack-300'
                >
                    <div className='flex flex-row items-center gap-2'>
                        <VscSignOut />
                        <span>Logout</span>
                    </div>

                </button>

            </div>

        </div>

        {confirmationModal && <ConfirmationModal modalData={confirmationModal}/>}
    </div>
  )
}
