import React, { useEffect, useState } from 'react';
import logo from '../../assets/Logo/Logo-Full-Light.png';
import { Link, matchPath, useLocation } from 'react-router-dom';
import { NavbarLinks } from '../../data/navbar-links';
import { useSelector } from 'react-redux';
import { AiOutlineShoppingCart } from "react-icons/ai";
import { ProfileDropDown  } from '../core/Auth/ProfileDropDown';
import { apiConnector } from '../../services/apiconnector';
import { categories } from '../../services/apis';

const Navbar = () => {

    const instructor = "Instructor";

    const {token} = useSelector( (state) => state.auth);
    const {user} = useSelector( (state) => state.profile);
    const {totalItems} = useSelector( (state) => state.cart);

    const location = useLocation();

    const [subLinks, setSubLinks] = useState([]);

    const fetchSubLinks = async() => {
        try{
            const result = await apiConnector("GET",categories.CATEGORIES_API);
            setSubLinks(result.data.data);
            console.log("Printing sub links result : ", result);
        } catch(error) {
            console.log("Error while making API call to fetch all categories : ",error);
        }
    }

    useEffect( () => {
        fetchSubLinks();
    }, [])

    const matchRoute = (route) => {
        return matchPath({path:route}, location.pathname);
    }

  return (
    <div className='flex h-14 justify-center items-center border-b-[1px] border-b-richblack-700'>
        <div className='flex items-center justify-between w-11/12 max-w-maxContent'>

            <Link to="/">
                <img src={logo} width={160} height={32}/>
            </Link>

            {/* Nav Links */}
            <nav>
                <ul className='flex gap-x-6 text-richblack-25 cursor-pointer'>
                    {
                        NavbarLinks.map( (link,index) => {
                            return (
                                <li key={index}>
                                    {
                                        link.title === "Catalog" ? (
                                                <div>
                                                    <p>{link.title}</p>
                                                </div>
                                            ) : (
                                            <Link to={link?.path}>
                                                <p className={`${matchRoute(link?.path) ? "text-yellow-25" : "text-richblack-25"}`}>
                                                    {link.title}
                                                </p>
                                            </Link>
                                        )
                                    }
                                </li>
                            );
                        })
                    }
                </ul>
            </nav>

            {/* login/signup/dashboard */}
            <div className='flex gap-x-4 items-center'>
                {
                    user && user?.accountType != instructor && (
                        <Link to="/dashboard/cart" className='relative'>
                            <AiOutlineShoppingCart />
                            {
                                totalItems > 0 && (
                                    <span>
                                        {totalItems}
                                    </span>
                                )
                            }
                        </Link>
                    ) 
                }
                {
                    token === null && (
                        <Link to="/login">
                            <button className='border border-richblack-700 bg-richblack-800 px-3 py-1
                            text-richblack-100 rounded-md'>
                                Log in
                            </button>
                        </Link>
                    )
                }
                {
                    token === null && (
                        <Link to="/signup">
                            <button className='border border-richblack-700 bg-richblack-800 px-3 py-1
                            text-richblack-100 rounded-md'>
                                Sign Up
                            </button>
                        </Link>
                    )
                }
                {
                    token != null && <ProfileDropDown/>
                }
            </div>

        </div>
    </div>
  )
}

export default Navbar;
