import React from 'react';
import * as Icons from 'react-icons/vsc';
import { useDispatch } from 'react-redux';
import { NavLink, matchPath, useLocation } from 'react-router-dom';

export const SidebarLink = ({ link, iconName }) => {
    // Get the specific icon component based on iconName
    const Icon = Icons[iconName];

    const location = useLocation();
    const dispatch = useDispatch();

    const matchRoute = (route) => {
        return matchPath(location.pathname, { path: route });
    }

    return (
        <NavLink
            to={link.path}
            className={`relative px-8 py-2 text-sm font-medium 
                ${matchRoute(link.path) ? "bg-yellow-100" : "bg-opacity-0"}`}
        >
            <span className={`absolute left-0 top-0 h-full w-[0.2rem] bg-yellow-50 
                ${matchRoute(link.path) ? "opacity-100" : "opacity-0"}`}
            ></span>
            <div className='flex flex-row items-center gap-2'>
                <Icon className="text-lg" />
                <span>{link.name}</span>
            </div>
        </NavLink>
    );
}
