import React from 'react';
import { FaArrowRight } from "react-icons/fa";
import {Link} from "react-router-dom";
import { HighlightText } from '../components/core/HomePage/HighlightText';

const Home = () => {
  return (
    <div className='relative mx-auto flex flex-col w-11/12 items-center justify-between text-white'>
        {/* Section 1 */}
        <div>

            <Link to={"/signup"}>
                <div className='group mt-16 p-1 mx-auto rounded-full bg-richblack-800 font-bold text-richblack-200
                transition-all duration-200 hover:scale-95 w-fit'>
                    <div className='flex flex-row gap-2 rounded-full items-center justify-between px-[10px] py-[5px]
                    transition-all duration-200 group-hover:bg-richblack-900'>
                        <p>Become an instructor</p>
                        <FaArrowRight />
                    </div>
                </div>
            </Link>

            <div className='text-center text-4xl font-semibold mt-7'>
                Empower Your Future with 
                <HighlightText text={"Coding Skills"} />
            </div>

            <div className='w-[70%] text-center mx-auto'>
                With our online coding courses, you can learn at your own pace, from anywhere in the world, and get access to a wealth of resources, including hands-on projects, quizzes, and personalized feedback from instructors.
            </div>

        </div>

        {/* Section 2 */}

        {/* Section 3 */}

        {/* Footer */}
    </div>
  )
};

export default Home;
