import React, { useState } from 'react';
import logo from '../../assets/Logo/Logo-Full-Light.png';
import { Link, matchPath } from 'react-router-dom';
import { NavbarLinks } from '../../data/navbar-links';

const Navbar = () => {

    const [activeLink, setActiveLink] = useState(NavbarLinks[0].title);

    const updateActiveLink = (value) => {
        setActiveLink(value);
    }

    const matchRoute = (route) => {
        return matchPath({path:route}, location.pathname);
    }

  return (
    <div className='flex h-14 justify-center items-center border-b-[1px] border-b-richblack-700'>
        <div className='flex items-center justify-between w-11/12 max-w-maxContent'>

            <Link to="/">
                <img src={logo} width={160} height={32}/>
            </Link>

            {/* Nav Links */}
            <nav>
                <ul className='flex gap-x-6 text-richblack-25 cursor-pointer'>
                    {
                        NavbarLinks.map( (link,index) => {
                            return (
                                <li key={index}>
                                    {
                                        link.title === "Catalog" ? (<div></div>) : (
                                            <Link to={link?.path}>
                                                <p className={`${matchRoute(link?.path) ? "text-yellow-25" : "text-richblack-25"}`}>
                                                    {link.title}
                                                </p>
                                            </Link>
                                        )
                                    }
                                </li>
                            );
                        })
                    }
                </ul>
            </nav>

        </div>
    </div>
  )
}

export default Navbar;
