import React from 'react'
import { HighlightText } from './HighlightText'
import know_your_progress from '../../../assets/Images/Know_your_progress.png';
import compare_with_others from '../../../assets/Images/Compare_with_others.png';
import plan_your_lessons from '../../../assets/Images/Plan_your_lessons.png';

export const LearningLanguageSection = () => {
  return (
    <div className='flex flex-col gap-5 mt-[130px] mb-24'>

      <div className='text-4xl text-center font-semibold'>
        Your swiss knife for
        <HighlightText text={"learning any language"}/>
      </div>

      <div className='text-center text-richblack-600 mx-auto text-base font-medium w-[70%]'>
        Using spin making learning multiple languages easy. with 20+ languages realistic voice-over, progress tracking, custom schedule and more.
      </div>

      <div className='mx-auto flex flex-row items-center justify-center mt-5 gap-[0px]'>


        <img src={know_your_progress}/>
        <img src={compare_with_others}/>
        <img src={plan_your_lessons}/>

      </div>

    </div>
  )
}
