import React, { useEffect, useState } from 'react'
import { useForm } from 'react-hook-form'
import { useDispatch, useSelector } from 'react-redux';

export const CourseInformationForm = () => {

  const {
    register,
    handleSubmit,
    setValue,
    getValues,
    fromState: {errors}
  } = useForm();

  const dispatch = useDispatch();
  const {course, editCourse} = useSelector((state) => state.course);
  const [loading,setLoading] = useState(false);
  const [courseCategories,setCourseCategories] = useState([]);

  useEffect(() => {
    const getCategories = async() => {
      setLoading(true);
      const categories = await fetchCourseCategories();
      // const categories = ['Python','Java','C++'];
      if(categories.length > 0) {
        setCourseCategories(categories);
      }
      setLoading(false);
    }

    if(editCourse) {
      setValue("courseTitle",course.courseName);
      setValue("courseShortDesc",course.courseDescription);
      setValue("coursePrice",course.price);
      setValue("courseTags",course.tag);
      setValue("courseBenefits",course.whatYouWillLearn);
      setValue("courseCategory",course.category);
      setValue("courseRequirements",course.instructions);
      setValue("courseImage",course.thumbnail);
    }

    getCategories();
  });

  const onSubmit = async(data) => {

  }

  return (
    <form
     onSubmit={handleSubmit(onSubmit)}
     className='rounded-md bg-richblack-800 border-r-richblack-700 p-6 space-y-8'
    >

      <div>
        <label>
          Course Title<sup>*</sup>
        </label>
        <input
          id='courseTitle'
          placeholder='Enter course title'
          {...register("courseTitle", {required:true})}
          className='w-full'
        />
        {
          errors.courseTitle && (
            <span>Course title is required<sup>**</sup></span>
          )
        }
      </div>

    </form>
  )
}
