import React from 'react';

// Importing React Icons
import { HiUsers } from "react-icons/hi";
import { ImTree } from "react-icons/im";

export const CourseCard = ({cardData, currentCard, setCurrentCard}) => {
  setCurrentCard(cardData[0].heading);
  return (
    <div className='w-[360px] lg:w-[30%]'
    onClick={() => setCurrentCard(cardData?.heading)}>

      {/* Upper Level */}
      <div>

        <div>
          {cardData.heading}
        </div>

        <div>
          {cardData.description}
        </div>

      </div>

      {/* Lower Level */}
      <div>

        <div className='w-full h-1 border-dashed border-richblack-600'></div>

        <div className='flex flex-row items-center justify-around'>

          <div className='flex flex-row items-center justify-center'>
            <HiUsers/>
            <div>{cardData.level}</div>
          </div>

          <div className='flex flex-row items-center justify-center'>
            <ImTree/>
            <div>{cardData.lessionNumber}<span>Lession</span></div>
          </div>

        </div>

      </div>

    </div>
  )
}
