import React from 'react'
import { useSelector } from 'react-redux';

export const Cart = () => {

    const {total, totalItems} = useSelector((state) => state.auth);

  return (
    <div>
        <h1>My Cart</h1>
        <p>{totalItems} courses in cart</p>
        {
            total>0 ? (
                <div>
                    <RenderCartCourses/>
                    <RenderTotalAmount/>
                </div>
            ) : (
                <p>Your cart is empty!</p>
            )
        }
    </div>
  )
}
